import '../node_modules/bootstrap/dist/css/bootstrap.css';
import Header from './Header';
import Footer from './Footer';
import './common.css';
import { useEffect, useState} from 'react';

function Dashboard(){
    const [emps,setEmps] = useState([]);
    const [emp,setEmp] = useState({ENo:0,EName:"", EAddres:""});

    OntextChange = (args)=>{
        var copyOfemp = {...emp};
        copyOfemp[args.target.name] = args.target.value;
        setState(copyOfemp);
    }
    console.log("Dashboard return(render) is being called");
    return (<>
                <Header></Header>
                <hr></hr>
                <div className = "myTable">
                    <div class = "form-group">
                        <input type ="number" className = "form-control"
                            name = "ENo" 
                            placeholder = "Enter No"
                            value = {emp.ENo}
                            onChange = {OntextChange}></input>
                    </div>
                    <div className = "form-group">
                        <input type = "text" className = "form-control"
                            name = "EName"
                            placeholder = "Enter EName"
                            value = {emp.Ename}
                            onChange={OntextChange}></input>
                    </div>
                    <div className = "form-group">
                        <input type = "text" className = "form-control"
                            name = "EAddress"
                            placeholder = "Enter EAddress"
                            value = {emp.EAddress}
                            onChange = {OntextChange}></input>
                    </div>
                    <button className = "btn btn-primary"
                            onClick = {insert}>Add Record</button>
                    <button className = "btn btn-success"
                            onClick = {update}
                            style = {{margin:10}}>Update Record</button>
                </div>            

                <hr></hr>

            </>)


}
