import React, { Component } from 'react';
import '../node_modules/bootstrap/dist/css/bootstrap';
import Header from './Header';
import Footer from './Footer';
import './common.css';

class Dashboard extends Component{
    state = { emps : [],
              emp :{ENo:0,Ename:"",EAddress:""},
              message:"",
              searchText:""
            }

    onTextchange= (args)=>{
        debugger;
        var copyOfEMp = {...this.state.Emp};
        copyOfEmp[args.target.name] = args.target.value;
        this.setState({emp:copyOfEMp})
    }
    render()
    {
        debugger;
        console.log("Dashboard render is getting called.");
        return(
            <>
                <Header></Header>
                <hr></hr>
                <div className= "myTable">
                    <div className = "form-group">
                        <input type = "number" className = "form-control"
                        name = "ENo"
                        placeholder = "ENo"
                        value = {this.state.emp.ENo}
                        onChange = {this.onTextchange}/>
                    </div>
                    <div className = "form-group">
                        <input type = "text" className = "form-control"
                        name = "EName"
                        placeholder = "EName"
                        value = {this.state.emp.Ename}
                        onChange={this.onTextchange}/>
                    </div>
                    <div className = "form-group">
                        <input type = "text" className = "form-control"
                        name = "EAddress"
                        placeholder = "EAddress"
                        value = {this.state.emp.EAddress}
                        onChange = {this.onTextchange}/>
                    </div>
                    <button className = "btn btn-primary"
                            onClick={this.insert}>
                                Add Record
                    </button>
                    <button className = "btn btn-succes"
                            onClick = {this.update}>
                                Update Record
                    </button>
                </div>

            </>
        )
    }

}
