import '../node_modules/bootstrap/dist/css/bootstrap.css';
import Header from './Header';
import Footer from './Footer';
import './common.css';
import { useEffect, useState} from 'react';

function Dashboard(){
    const [emps,setEmps] = useState([]);
    const [emp,setEmp] = useState({ENo:0,EName:"", EAddres:""});
    const [searchText,setSearchText] = useState("");

    const OntextChange = (args)=>{
        var copyOfemp = {...emp};
        copyOfemp[args.target.name] = args.target.value;
        setEmp(copyOfemp);
    }
    const onSearch=(args)=>{
        debugger;
        setSearchText(args.target.value);
    }

    const select = ()=>{
        var helper = new XMLHttpRequest();
        helper.onreadystatechange= ()=>{
            if (helper.readyState == 4 && helper.status == 200)
            {   
                debugger;
                var result = JSON.parse(helper.responseText)
            } 
        }
    }

    console.log("Dashboard return(render) is being called");
    return (<>
                <Header></Header>
                <hr></hr>
                <div className = "myTable">
                    <div class = "form-group">
                        <input type ="number" className = "form-control"
                            name = "ENo" 
                            placeholder = "Enter No"
                            value = {emp.ENo}
                            onChange = {OntextChange}></input>
                    </div>
                    <div className = "form-group">
                        <input type = "text" className = "form-control"
                            name = "EName"
                            placeholder = "Enter EName"
                            value = {emp.Ename}
                            onChange={OntextChange}></input>
                    </div>
                    <div className = "form-group">
                        <input type = "text" className = "form-control"
                            name = "EAddress"
                            placeholder = "Enter EAddress"
                            value = {emp.EAddress}
                            onChange = {OntextChange}></input>
                    </div>
                    <button className = "btn btn-primary"
                            onClick = {insert}>Add Record</button>
                    <button className = "btn btn-success"
                            onClick = {update}
                            style = {{margin:10}}>Update Record</button>
                </div>            

                <hr></hr>
                <div className = 'myTable alert alert-warning'>
                    <h1>{message}</h1>
                </div>
                <hr></hr>

                <div className = 'table-responsive'>
                    <div style = {{floar:"right",marginRight:300}}>Search:
                        <input type="text" 
                        className = "input" 
                        value = "searchText"
                        onChange = {onSearch}></input>
                        <br></br>
                    </div>
                <table className = "table table-bordered table-striped myTable">
                    <tbody>
                        {
                            emps.map((emp)=>{
                                if (searchText!="")
                                {
                                    debugger;
                                    if (emp.EAddress.toLowerCase().includes(searchText.toLoweCase()))
                                    {
                                        return <tr key = {emp.ENo}>
                                            <td>{emp.ENo}</td>
                                            <td>{emp.EName}</td>
                                            <td>{emp.EAddress}</td>
                                            <td>
                                                <button className = 'btn btn-danger'
                                                    onClick = {()=>{
                                                        deleteEmp(emp.ENo)
                                                    }}>DELETE</button>
                                            </td>
                                            <td>
                                                <button className = 'btn btn-warning'
                                                    onClick = {()=>{
                                                        edit(emp.ENo)
                                                    }}>EDIT</button>
                                            </td>
                                        </tr>     
                                    }
                                    else
                                    {
                                        return <tr key = {emp.ENo}>
                                            <td>{emp.ENo}</td>
                                            <td>{emp.EName}</td>
                                            <td>{emp.EAddress}</td>
                                            <td>
                                                <button className = 'btn btn-danger'
                                                    onClick = {()=>{
                                                        deleteEmp(emp.ENo)
                                                    }}>DELETE</button>
                                            </td>
                                            <td>
                                                <button class = 'btn btn-warning'
                                                onClick = {()=>{
                                                    edit(emp.ENo)
                                                }}>EDIT</button>
                                            </td>
                                        </tr>
                                    }
                                }
                            })
                        }
                    </tbody>
                </table>

                </div>
                <hr></hr>
                <Footer></Footer>

            </>);
}

export default Dashboard;
