import '../node_modules/bootstrap/dist/css/bootstrap.css';
import Header from './Header';
import Footer from './Footer';
import './common.css';
import {useEffect, useState} from 'react';
import DashboardRow from './DashboardRow';

function Dashboard(){
    const [emps,setEmps] = useState([]);
    const [emp,setEmp] = useState({ENo:0,EName:"", EAddres:""});
    const [message,setMessage] =  useState("");
    const [searchText,setSearchText] = useState("");

    useEffect(()=>{
        console.log("inside component did mount (useEffect hook with no dependancy)");
        select();
    },[])

    useEffect(()=>
    {
        console.log("Component Did Update is called..")
    }, [emps, emp, message, searchText]);

    useEffect(()=>{
        setTimeout(()=>{
            setMessage("")
        },3000)
    },[message])

    const OntextChange = (args)=>{
        var copyOfemp = {...emp};
        copyOfemp[args.target.name] = args.target.value;
        setEmp(copyOfemp);
    }
    const onSearch=(args)=>{
        debugger;
        setSearchText(args.target.value);
    }


    const select = ()=>{
        var helper = new XMLHttpRequest();
        helper.onreadystatechange= ()=>{
            if (helper.readyState == 4 && helper.status == 200)
            {   
                debugger;
                var result = JSON.parse(helper.responseText)
                var receivedEmps = result;
                console.log("setting EMps to array received from Node server")
                debugger;
                setEmps(receivedEmps);
            } 
        }
        helper.open("GET","http://127.0.0.1:9999/emps");
        helper.send();
    }

    const deleteEmp=(ENo)=>{
        debugger;
        var helper = new XMLHttpRequest();
        helper.onreadystatechnage = ()=>{
            if (helper.readyState==4 && helper.status==200)
            {
                var responseReceived = JSON.parse(helper.responseText)
                if (responseReceived.affectedRows!=undefined && responseReceived.affectedRows>0)
                {
                    setMessage("Deletion Successful");
                    select();
                }
                else
                {
                    setMessage("Something went wrong!")
                }
            }
        }
        helper.open("DELETE","http://127.0.0.1:9999/emps/"+ENo);
        helper.send();
    }

    const insert = ()=>{
        // to be sent to node server
        var helper = new XMLHttpRequest();
        helper.onreadystatechange=()=>
        {
            if (helper.readyState==4 && helper.status==200)
            {
                var responseReceived = JSON.parse(helper.responseText)
                if (responseReceived.affectedRows != undefined && responseReceived.affectedRows>0)
                {
                    setMessage("Record Added Successfully")
                    select();
                }
                if (responseReceived.warningCount == 1){
                    getWarning();
                }
                else
                {
                    setMessage("Something went Wrong!")
                }
            }
        }
        helper.open("POST","http://127.0.0.1:9999/emps")
        helper.setRequestHeader("Content-Type", "application/json")
        helper.send(JSON.stringify(emp));
    }

    const getWarning = ()=>{
        var helper = new XMLHttpRequest();
        helper.onreadystatechange=()=>
        {
            if (helper.readyState==4 && helper.status==200)
            {
                var responseReceived = JSON.parse(helper.responseText)
                debugger;
                if (responseReceived[0].Code==1062)
                {
                    setMessage("Employee Number is already taken. Kindly use other.");
                }
            }
        }
        helper.open("POST","http://127.0.0.1:9999/emps/warning")
        helper.setRequestHeader("Content-Type", "application/json")
        helper.send(JSON.stringify(emp));
    }

    const edit = (ENo)=>{
        debugger;
        console.log("You need to find record with ENO = "+ ENo + "from - ");
        console.log(emps);
        emps.map((empToEdit)=>{
            if (empToEdit.ENo == ENo)
            {
                setMessage("Record Found!!")
                var copyOfEmpToEditFromArray = {...empToEdit};
                setEmp(copyOfEmpToEditFromArray);
                return;
            }
        })
    }
    const update = (ENo)=>{
        var helper = new XMLHttpRequest();
        helper.onreadystatechange = ()=>{
            if (helper.readyState==4 && helper.status==200)
            {
                var responseReceived = JSON.parse(helper.responseText)
                if (responseReceived.affectedRow != undefined && responseReceived.affectedRows>0)
                {
                    setMessage("Record Updated Successfully")
                    select();
                    setEmp({ENo:0,EName:"",EAddress:""});
                }
                else
                {
                    console.log("Something went Wrong!")
                }
            }
        }
        helper.open("PUT","http://127.0.0.1:9999/emps/"+ENo);
        helper.setRequestHeader("Conent-Type","application/json");
        helper.send(JSON.stringify(emp));
    }


    console.log("Dashboard return(render) is being called");
    return (<div>  
                <Header></Header>
                <hr></hr>

                <div className = "myTable">
                    <div className = "form-group">
                        <input type ="number" className = "form-control"
                            name = "ENo" 
                            placeholder = "Enter No"
                            value = {emp.ENo}
                            onChange = {OntextChange}></input>
                    </div>
                    <div className = "form-group">
                        <input type = "text" className = "form-control"
                            name = "EName"
                            placeholder = "Enter EName"
                            value = {emp.Ename}
                            onChange={OntextChange}></input>
                    </div>
                    <div className = "form-group">
                        <input type = "text" className = "form-control"
                            name = "EAddress"
                            placeholder = "Enter EAddress"
                            value = {emp.EAddress}
                            onChange = {OntextChange}></input>
                    </div>
                    <button className = "btn btn-primary"
                            onClick = {insert}>Add Record</button>
                    <button className = "btn btn-success"
                            onClick = {update}
                            style = {{margin:10}}>Update Record</button>
                </div>            

                <hr></hr>
                <div className = 'myTable alert alert-warning'>
                    <h1>{message}</h1>
                </div>
                <hr></hr>

                <div className = 'table-responsive'>
                    <div style = {{float:"right",marginRight:300}}>Search:
                        <input type="text" 
                        className = "input" 
                        value = {searchText}
                        onChange = {onSearch}></input>
                        <br></br>
                    </div>
                <table className = "table table-bordered table-striped myTable">
                    <tbody>
                        {
                            emps.map((emp1)=>{
                                if (searchText!="")
                                {
                                    debugger;
                                    if (emp1.EAddress.toLowerCase().includes(searchText.toLowerCase()))
                                    {
                                        //we are using props to pass the values and make code more manageable
                                        return  <DashboardRow  key={emp.ENo} 
                                                emp={emp}
                                                deleteEmp={deleteEmp}
                                                edit={edit}/>    
                                    }else {return;}}

                                else  
                                {        
                                    debugger;
                                    return  <DashboardRow  key={emp.ENo} 
                                                emp={emp}
                                                deleteEmp={deleteEmp}
                                                edit={edit}/> 

                                }
                            })
                        }
                    </tbody>
                </table>

                </div>
                <hr></hr>
                <Footer></Footer>

            </div>);
}

export default Dashboard;
